import { useState, useEffect } from 'react';
import { getTodosFromBackend } from '../../utils/getTodosFromBackend';

export default function useLocalStorage(key, initialValue) {
  const [todos, setTodos] = useState(() => { // useState() hook in dem die ToDos gespeichert werden
    const storedTodos = JSON.parse(localStorage.getItem(key)); // getTodos from local storage, wandelt den String in ein Array um
    return storedTodos || initialValue || []; // wenn storedTodos nicht leer ist, dann gebe storedTodos zurück, sonst initialValue
  }); 



  useEffect(() => { // useEffect() hook wird ausgeführt, wenn sich die ToDos ändern
    localStorage.setItem(key, JSON.stringify(todos)); //setTodos to local storage, wandet das Array in einen String um
  }, [key, todos]); // sobald sich key oder todos ändern, wird useEffect() ausgeführt


  //Laden der ToDos aus der Datenbank
  const loadTodosFromBackend = async () => {
    try {
      const backendTodos = await getTodosFromBackend();
      setTodos((prevTodos) => [...prevTodos, ...backendTodos]);
    } catch (error) {
      console.log("Ooops, something went wrong, while loading the todos from the backend: ");
    }
  };

  useEffect(() => {
    loadTodosFromBackend();
  }, []); // Dieses Abhängigkeitsarray ist leer, damit der Effekt nur einmal ausgeführt wird
  // und zwar nur beim ersten rendern der Komponente


  return [todos, setTodos]; // gibt die ToDos und die Funktion zum setzen der ToDos zurück + den Key
}
