import React from 'react'
import ToDo from './createToDo'
import InputAndReturn from './takeInputvalueAndReturn'
import useLocalStorage from './customHooks/useLocalStorage'
import ToTopButton from './nicht verwendet/toTheTopButton'




export default function AppContainer() {
  //Costum Hooks
  const [todos, setTodos] = useLocalStorage('todos', []); // wird mit ToDos aus local storage initialisiert, sind keine vorhanden wird ein leeres Array übergeben
  const [todosCount, setTodosCount] = useLocalStorage('todosCount', todos.length); // die Anzahl der todos wir aus dem LS geholt. Sind kei


  return (
    <div className="appContainer">
    <h1 id="top">ToDo</h1>
    {/* // an die Komponente InputAndReturn werden die ToDos und die Funktion zum setzen der ToDos übergeben */}
    <InputAndReturn todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
    {/* // an die Komponente ToDo werden die ToDos übergeben, die diese dann anzeigt */}
    {/* + Inline Function onDelete */}
    <ToDo todos={todos} onDelete={(index) =>{
      const newTodos = [...todos]; // kopiert das Array todos als newTodos
      newTodos.splice(index, 1); // mit splice wird das ToDo an der Stelle index gelöscht. 1 steht für die Anzahl der zu löschenden Elemente
      setTodos(newTodos); // setzt das neue Array newTodos als todos
    }}/>
    <ToTopButton/>
    </div>
  );
}
