import React, { Component } from 'react';
import CreateIcon from '@mui/icons-material/Create';

class FocusToBarOnButtonClick extends Component {
  constructor(props) {
    super(props);
    this.buttonRef = React.createRef();
  }

  focusInputField = () => {
    console.log("Focusing on input field");
    const { todoTextRef } = this.props;
    if (todoTextRef && todoTextRef.current) {
      todoTextRef.current.focus();
    }
  }

  render() {
    return (
      <div>
        <button ref={this.buttonRef} className='cornerButton' onClick={this.focusInputField}>
          <CreateIcon />
        </button>
      </div>
    );
  }
}

export default FocusToBarOnButtonClick;
