import React, { useRef } from "react";
import TextField from "@mui/material/TextField";
import { createTheme, ThemeProvider } from "@mui/material/styles";


const theme = createTheme({
  palette: {
    primary: {
      main: "#ff79c6",
    },
    text: {
      primary: "#ffff",
      secondary: "#ffff",
    },
  },
});

export default function InputAndReturn({todos, setTodos}) {
  // variable todoTextRef mit useRef() erstellen
  const todoTextRef = useRef();

  function InputValueToToDoList(){
    const newTodo = todoTextRef.current.value;
    console.log("wird ausgeführt");
    if (newTodo.trim() !== "") { // Nur wenn das Eingabefeld nicht leer ist, füge das To-Do hinzu
      setTodos([...todos, newTodo]); // Füge das neue To-Do dem bestehenden Array [...todos] hinzu
      todoTextRef.current.value = ""; // textfeld danach wieder leeren
    }
  }


  // clickButtonThenInput() function bei Button onClick
  function clickButtonThenInput(e) {  // wird mit event ausgeführt.
    e.preventDefault(); // e.preventDefault() verhindert, dass die Seite neu geladen wird
    console.log("click");
    // console.log(todoTextRef) greift auf den Wet des Textfields zu (durch den refHook inputRef={todoTextRef})
    console.log(todoTextRef.current.value);
    
    InputValueToToDoList();
  
  }

  return (
    <>
      <form className="inputField">
        <ThemeProvider theme={theme}>
          {/* ref hook todoTextRef im Textfield */}
          <TextField inputRef={todoTextRef} className="input" id="filled-basic" label="Add ToDo..." variant="filled" color="primary" inputProps={{ style: { color: "#ff79c6" } }}/>
        </ThemeProvider>
        {/* bei onClick wird clickButtonThenInput() ausgeführt */}
        <button onClick={(e)=>clickButtonThenInput(e)}>+</button>
      </form>
    </>
  );
}
