import { useState, useEffect } from 'react';

export default function useLocalStorage(key, initialValue, countKey) {
  const [todos, setTodos] = useState(() => { // useState() hook in dem die ToDos gespeichert werden
    const storedTodos = JSON.parse(localStorage.getItem(key)); // getTodos from local storage. JSON.parse() wandelt den String in ein Array um
    return storedTodos || initialValue; // wenn storedTodos nicht leer ist, dann gebe storedTodos zurück, sonst initialValue
  }); 

  const [count, setCount] = useState(()=>{
    const storedCount = JSON.parse(localStorage.getItem(countKey));
    return storedCount || initialValue;
  });


  const deleteTodoFromLocalStorage = (todoToDelete) => {
    const updatedTodos = todos.filter((todo) => todo !== todoToDelete);
    setTodos(updatedTodos);
    localStorage.setItem(key, JSON.stringify(updatedTodos));
  };

  useEffect(() => { // useEffect() hook wird ausgeführt, wenn sich die ToDos ändern
    if (count < 20){ // wenn count kleiner als 20 ist...
    localStorage.setItem(key, JSON.stringify(todos)); //setTodos to local storage. JSON.stringify() wandelt das Array in einen String um
    localStorage.setItem(countKey, JSON.stringify(count)); //setCount to local storage
    }
  }, [key, todos, count, countKey]); // key, todos, count und countKey sind die dependencies. Wenn sich diese ändern, wird useEffect() ausgeführt
  return [todos, setTodos, count, deleteTodoFromLocalStorage];
}
