import React, { useRef } from 'react';
import CreateIcon from '@mui/icons-material/Create';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import InputAndReturn from './takeInputvalueAndReturn';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 300,
  backgroundColor: '#44475a',
  border: '0.5px solid #6272a4',
  boxShadow: '24px',
  borderRadius: '25px',
  padding: '16px',
  color: '#ffffff',
  textAlign: 'center',

};




export default function ToDoPopUp({todos, setTodos, todosCount, setTodosCount}) {
  const topRef = useRef(null);
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


  return (



    <div>
      <button className='cornerButton' onClick={handleOpen}>
        <CreateIcon />
      </button>
    

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
  
        <Box sx={style}>
          <h1>Add To-Do</h1>
          <InputAndReturn todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
          
        </Box>

      </Modal>
    </div>
  );
}
