import React from "react";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material";
import SelfImprovementIcon from '@mui/icons-material/SelfImprovement';
import DeleteToDo from "./deleteToDo";
const theme = createTheme({
  typography: {
    fontFamily: ["Caveat", "cursive"].join(","),
  },
});


// die Komponente ToDo bekommt die ToDos übergeben und zeigt diese an
export default function ToDo({todos, onDelete, todosCount}) {
  return (
    <div className="toDoBoard">
      {/* wenn das Array todos nicht leer ist, dann erstelle aus jedem ToDo im Array ein div */}
      {todos.length > 0 ? ( 
         todos.map((todo, index) => ( 
          <React.Fragment key={index}> {/*Gibt jedem ToDo einen Key*/}
          <div className="toDo">
              <ThemeProvider theme={theme}>
                <Typography variant="h4" component="div" gutterBottom>
                  {todo}
                  <DeleteToDo onDelete={()=>onDelete(index)} /> {/*Komponente DeleteToDo wird gerendert*/}
                </Typography>
              </ThemeProvider>
          </div>
          </React.Fragment>
         ))
      ) : (
        
        <div className="relax">
          <SelfImprovementIcon style={{fontSize: "5rem"}}/>
          <p>Nothing to do. Sit back an relax.<br/>Put your feet up! </p>
          </div>

      )}
     
    </div>
  );
}
