import { useState, useEffect } from 'react';

export default function useLocalStorage(key, initialValue, countKey) {
  const [todos, setTodos] = useState(() => {
    const storedTodos = JSON.parse(localStorage.getItem(key));
    return storedTodos || initialValue;
  });

  const [count, setCount] = useState(() => {
    const storedCount = JSON.parse(localStorage.getItem(countKey));
    return storedCount || initialValue; // Hier Initialwert verwenden, wenn count nicht im Local Storage vorhanden ist.
  });

  const deleteTodoFromLocalStorage = (todoToDelete) => {
    const updatedTodos = todos.filter((todo) => todo !== todoToDelete);
    setTodos(updatedTodos);
    localStorage.setItem(key, JSON.stringify(updatedTodos));
  };

  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(todos));
    localStorage.setItem(countKey, JSON.stringify(count)); // Speichere count im Local Storage
  }, [key, todos, count, countKey]);

  return [todos, setTodos, count, deleteTodoFromLocalStorage];
}
