import React, { useRef } from "react";
import TextField from "@mui/material/TextField";
import AddTaskIcon from '@mui/icons-material/AddTask';
import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#ff79c6",
    },
    text: {
      primary: "#ffff",
      secondary: "#ffff",
    },
  },
});

export default function InputAndReturn({ todos, setTodos }) {
  const todoTextRef = useRef();

  function InputValueToToDoList() {
    const newTodo = todoTextRef.current.value;
    if (newTodo.trim() !== "") {
      setTodos([...todos, newTodo]);
      localStorage.setItem("todos", JSON.stringify([...todos, newTodo]));
      todoTextRef.current.value = "";
    }
  }

  function clickButtonThenInput(e) {
    e.preventDefault();
    InputValueToToDoList();
  }

  return (
    <>
      <form className="inputField">
        <ThemeProvider theme={theme}>
          <TextField inputRef={todoTextRef} className="input" id="filled-basic" label="Add ToDo..." variant="filled" color="primary" inputProps={{ style: { color: "#ff79c6" } }}/>
        </ThemeProvider>
        <button onClick={clickButtonThenInput}><AddTaskIcon/></button>
      </form>
    </>
  );
}
