// createAppContainer.jsx
import React from 'react'
import ToDo from './createToDo'
import InputAndReturn from './takeInputvalueAndReturn'
import useLocalStorage from './customHooks/useLocalStorage'
import ToDoPopUp from './toDoPopUpButton'
import { AppContainerWrap } from '../styled/AppContainerWrap'
import { Title } from '../styled/Title'
import { getTodosFromBackend } from '../utils/getTodosFromBackend';
import { create } from '@mui/material/styles/createTransitions'
import useMongoDBData from './customHooks/useMongoDBData'


export default function AppContainer() {
  //Costum Hooks
  const [todos, setTodos] = useMongoDBData(); // wird mit ToDos aus local storage initialisiert, sind keine vorhanden wird ein leeres Array übergeben
  // const [todosCount, setTodosCount] = useLocalStorage('todosCount', todos.length); // die Anzahl der todos wir aus dem LS geholt. Ist keine vorhanden wird die Länge des Arrays übergeben


  return (
    <AppContainerWrap>
    <Title>ToDo</Title>
    {/* // an die Komponente InputAndReturn werden die ToDos und die Funktion zum setzen der ToDos übergeben inkl Anzahl der Todos*/}
    <InputAndReturn todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
    {/* // an die Komponente ToDo werden die ToDos übergeben, die diese dann anzeigt */}
    {/* + Inline Function onDelete */}
    <ToDo todos={todos} onDelete={(index) =>{
      const newTodos = [...todos]; // kopiert das Array todos als newTodos
      newTodos.splice(index, 1); // mit splice wird das ToDo an der Stelle index gelöscht. 1 steht für die Anzahl der zu löschenden Elemente
      setTodos(newTodos); // setzt das neue Array newTodos als todos
    }}/>
    <ToDoPopUp todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
    </AppContainerWrap>
  );
}
