import React from 'react';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';

export default function DeleteToDo({onDelete, todos}) {

  const handleDeleteClick = () => {
    console.log("Trashicon clicked");
    onDelete(); // onDelete funtion wird aufgerufen um das ToDo zu löschen
    localStorage.setItem("todosCount", JSON.stringify(todos.length - 1));
  }

  return (
    <div className="trashicon" onClick={handleDeleteClick}>
        <DeleteForeverIcon/>
    </div>
  );
}
