import React, {useRef} from 'react'
import TextField from '@mui/material/TextField';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
        main: '#ff79c6',
    },
    text: {
        primary: "#ffff",
        secondary: "#ffff",
      },
  },
});



export default function InputAndReturn() {

  const todoTextRef = useRef();

    function clickLog(){
        console.log('click')
        console.log(todoTextRef.current.value);
        todoTextRef.current.value = '';
    }

  return (

    <>
    <form className='inputField'>
    <ThemeProvider theme={theme}>
    <TextField inputRef={todoTextRef} className="input" id="filled-basic" label="Add ToDo..." variant="filled" color="primary" inputProps={{ style: { color: "#ff79c6" } }}/>
    </ThemeProvider>
      <button onClick={clickLog} >+</button>

    </form>
    </>

  )
}
