import React, { useRef } from 'react';
import CreateIcon from '@mui/icons-material/Create';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import InputAndReturn from '../takeInputvalueAndReturn';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  color: '#ffff',
  bgcolor: '#44475a',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


export default function ToTopButton({todos, setTodos, todosCount, setTodosCount}) {
  const topRef = useRef(null);
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


  const scrollToTop = () => {
    if (topRef.current) {
      topRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  console.log("ToTopButton rendered");

  return (



    <div>
      <button className='cornerButton' onClick={handleOpen}>
        <CreateIcon />
      </button>
      <div ref={topRef}></div>

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </Typography>
          <InputAndReturn todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
          
        </Box>
      </Modal>
    </div>
  );
}
