import React, { useRef } from "react";
import TextField from "@mui/material/TextField";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#ff79c6",
    },
    text: {
      primary: "#ffff",
      secondary: "#ffff",
    },
  },
});

export default function InputAndReturn() {
  // variable todoTextRef mit useRef() erstellen
  const todoTextRef = useRef();

  function Input({setTodos}){
    //...
  }


  // clickLog() function bei Button onClick
  function clickLog() {
    console.log("click");
    // console.log(todoTextRef) greift auf den Wet des Textfields zu (durch den refHook inputRef={todoTextRef})
    console.log(todoTextRef.current.value);
    // textfeld danach wieder leeren
    todoTextRef.current.value = "";
    Input({setTodos});
  }



  return (
    <>
      <form className="inputField">
        <ThemeProvider theme={theme}>
          {/* ref hook todoTextRef im Textfield */}
          <TextField inputRef={todoTextRef} setTodos={setTodos} className="input" id="filled-basic" label="Add ToDo..." variant="filled" color="primary" inputProps={{ style: { color: "#ff79c6" } }}/>
        </ThemeProvider>
        {/* bei onClick wird clickLog() ausgef√ºhrt */}
        <button onClick={clickLog}>+</button>
      </form>
    </>
  );
}
