import React, { useEffect, useState } from 'react';
import ToDo from './createToDo';
import InputAndReturn from './takeInputvalueAndReturn';
import ToDoPopUp from './toDoPopUpButton';
import { AppContainerWrap } from '../styled/AppContainerWrap';
import { Title } from '../styled/Title';
import { getTodosFromBackend } from '../utils/getTodosFromBackend'; // Import the function

export default function AppContainer() {
    const [todos, setTodos] = useState([]); // Initialize todos as an empty array

    useEffect(() => {
        getTodosFromBackend(setTodos); // Fetch todos from the backend and update the state
    }, []);

    return (
        <AppContainerWrap>
            <Title>ToDo</Title>
            <InputAndReturn todos={todos} setTodos={setTodos} />
            <ToDo todos={todos} onDelete={(index) => {
                // Delete todos as needed
            }} />
            <ToDoPopUp todos={todos} setTodos={setTodos} />
        </AppContainerWrap>
    );
}
