import React, { useEffect, useRef } from 'react'
import ToDo from './createToDo'
import InputAndReturn from './takeInputvalueAndReturn'
import FocusToBarOnButtonClick from './focusToBarOnButtonClick'
import { useState } from 'react'



export default function AppContainer() {
  // useState() hook in dem die ToDos gespeichert werden
  const [todos, setTodos] = useState([]); // ist von Beginn an leer
  const todoTextRef = useRef(); // variable todoTextRef mit useRef()-hook erstellen

  useEffect(() => { // useEffect() hook wird ausgeführt, wenn sich die ToDos ändern
    const storedTodos = JSON.parse(localStorage.getItem("todos")); // getTodos from local storage
    if (storedTodos) { // wenn storedTodos nicht leer ist, dann
      setTodos(storedTodos); // füge die ToDos aus dem local storage in das Array ein (setTodos)
    }
  }, []); // [] wird übergeben, damit der useEffect() hook nur einmal ausgeführt wird

  return (
    <div className="appContainer">
    <h1>ToDo</h1>
    {/* // an die Komponente InputAndReturn werden die ToDos und die Funktion zum setzen der ToDos übergeben */}
    <InputAndReturn todos={todos} setTodos={setTodos}/>
    {/* // an die Komponente ToDo werden die ToDos übergeben, die diese dann anzeigt */}
    <ToDo todos={todos}/>
    </div>
  );
}
