import { useState, useEffect } from 'react';

export default function useLocalStorage( initialValue) {
  const [todos, setTodos] = useState(() => {
    const storedTodos = JSON.parse(localStorage.getItem()); // getTodos from local storage
    return storedTodos || initialValue; // wenn storedTodos nicht leer ist, dann gebe storedTodos zurück, sonst initialValue
  }); // useState() hook in dem die ToDos gespeichert werden



  useEffect(() => { // useEffect() hook wird ausgeführt, wenn sich die ToDos ändern
    localStorage.setItem(key, JSON.stringify(todos)); //setTodos to local storage
  }, [todos]);

  return [todos, setTodos];
}
