import { useState, useEffect } from 'react';

export default function useLocalStorage(key, initialValue, todosCount) {
  const [todos, setTodos] = useState(() => {
    const storedTodos = JSON.parse(localStorage.getItem(key)); // getTodos from local storage
    return storedTodos || initialValue; // wenn storedTodos nicht leer ist, dann gebe storedTodos zurück, sonst initialValue
  }); // useState() hook in dem die ToDos gespeichert werden


  useEffect(() => { // useEffect() hook wird ausgeführt, wenn sich die ToDos ändern
    localStorage.setItem(key, JSON.stringify(todos)); //setTodos to local storage
    localStorage.setItem(todosCountKey, JSON.stringify(count));
  }, [key, todos]);


  const [count, setCount] = useState(() => {
    const storedCount = JSON.parse(localStorage.getItem(todosCountKey)); // getTodos from local storage
      return storedCount !== null ? storedCount : initialValue;
  });


  return [todos, setTodos, count, setCount];
}
