import React, { useRef } from 'react';
import CreateIcon from '@mui/icons-material/Create';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import InputAndReturn from '../takeInputvalueAndReturn';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  color: '#ffff',
  bgcolor: '#44475a',
  border: '0.5px solid #6272a4',
  boxShadow: 24,
  borderRadius: '25px',
  p: 4,
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};


export default function ToTopButton({todos, setTodos, todosCount, setTodosCount}) {
  const topRef = useRef(null);
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);


  const scrollToTop = () => {
    if (topRef.current) {
      topRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  console.log("ToTopButton rendered");

  return (



    <div>
      <button className='cornerButton' onClick={handleOpen}>
        <CreateIcon />
      </button>
      <div ref={topRef}></div>

      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <InputAndReturn todos={todos} setTodos={setTodos} todosCount={todosCount} setTodosCount={setTodosCount}/>
          
        </Box>
      </Modal>
    </div>
  );
}
