import React, { useState } from 'react'
import ToDo from './createToDo'
import InputAndReturn from './takeInputvalueAndReturn'
import useLocalStorage from './customHooks/useLocalStorage'




export default function AppContainer() {
  // useState() hook in dem die ToDos gespeichert werden
  const [todos, setTodos] = useLocalStorage('todos', []); // todos wird mit useLocalStorage() hook initialisiert.
  // es werden die ToDos aus dem local storage geladen und in todos gespeichert, sind keine 
  // ToDos vorhanden, dann wird todos mit einem leeren Array initialisiert.

  const [count, setCount] = useState(0); // useState() hook in dem die Anzahl der ToDos gespeichert werden



  return (
    <div className="appContainer">
    <h1 id="top">ToDo</h1>
    {/* // an die Komponente InputAndReturn werden die ToDos und die Funktion zum setzen der ToDos übergeben */}
    <InputAndReturn todos={todos} setTodos={setTodos}/>
    {/* // an die Komponente ToDo werden die ToDos übergeben, die diese dann anzeigt */}
    <ToDo todos={todos}/>
    </div>
  );
}
